import './App.css';
import React, { useEffect, useState } from 'react';
import Child from './element'



function App() {
  const [names, setNames] = useState(['Greg', 'Sarah', 'Bobby', 'Kaitlyn', 'Tom']);
  const [newName, setNewName] = useState('Tom')

  const addName = (name) => {
    setNames(prev => [...prev,name])
  }

  useEffect(()=>{
    console.log('name added.')
  }, [names])
  return (
    <div>
      <div style={{padding:'20px'}}>

        <h1>header</h1>
        <form onSubmit={() => addName(newName)}>
          <input 
            type='text' 
            value={newName}
            onChange={event => setNewName(event.target.value)}>
          </input>
          <input type='submit'/>
        </form>


      </div>
      {names.map( (name,index) => {
        return <Child name={name} key={index}/>
        })
      }
    </div>
  );
}

export default App;
