import './App.css';
import React, { useEffect, useState } from 'react';
import Child from './element'



function App() {

  const [names, setNames] = useState(   []  );
  const [newName, setNewName] = useState('Tom')

  const addName = (name) => {
    setNames(prev => [...prev,name])
  }

  useEffect(()=>{
    console.log('name added.', newName)
  }, [names]);

  return (
    <div>
      <div style={{padding:'20px',textAlign:'center',background:'orange'}}>

        <h1 color='white'>Name Adder</h1>

        <h3>Add a name:</h3>
          <input 

            type='text' 
            value={newName}
            onChange={event => setNewName(event.target.value)}/>

          <button onClick= { () => addName(newName) }>
            Add name
          </button>



      </div>
      <div style={{padding:'20px',textAlign:'center'}}>
        {names.map( (name,index) => {
          return <Child name={name} number={index + 1} key={index}/>
          })
        }
      </div>
    </div>
  );
}

export default App;
