import './App.css';
import React, { useEffect, useState } from 'react';
import Child from './element'
import Button from '@material-ui/core/Button'


function App() {

  const [names, setNames] = useState(   []  );
  const [newName, setNewName] = useState('Tom')

  const addName = (name) => {
    setNames(prev => [...prev,name])
  }

  const removeName = (name) => {
    setNames(prev => prev.filter((i) => i !== name))
  }

  useEffect(()=>{
    console.log('name added.', newName)
  }, [names]);

  return (
    <div>
      <div style={{padding:'20px',textAlign:'center',background:'orange'}}>

        <h1 style={{color:"white"}}>Name Adder</h1>
      </div>
      <div style={{padding:'20px',textAlign:'center'}}>
        <h3>Add a name:</h3>
          <input 

            type='text' 
            value={newName}
            onChange={event => setNewName(event.target.value)}/>

          <Button onClick= { () => addName(newName) }>
            Add name
          </Button>
        {names.map( (name,index) => {
          return <Child name={name} remove={removeName} key={index}/>
          })
        }
      </div>
    </div>
  );
}

export default App;
